  flex-direction: column;
}

main#quarto-document-content {
  padding: 0 !important;
  margin: 0 !important;
  min-height: 70vh; /* Allow content to flow naturally */
}

/* Navigation zones - invisible hover areas */
.carousel-zone {
  position: fixed;
  top: 120px; /* Below header */
  height: calc(100vh - 200px); /* Leave space for header and footer */
  max-height: calc(100vh - 200px); /* Prevent extending into footer */
  z-index: 100;
  cursor: none; /* Hide default cursor */
  transition: background-color 0.2s ease;
  pointer-events: auto; /* Ensure click events work */
}

.carousel-zone-left {
  left: 15%;
  width: 30%;
}

.carousel-zone-right {
  right: 15%;
  width: 30%;
}

/* Cursor arrow that follows mouse */
.cursor-arrow {
  position: fixed;
  pointer-events: none;
  z-index: 9999;
  opacity: 0;
  transition: opacity 0.2s ease;
  transform: translate(-50%, -50%);
  display: flex;
  align-items: center;
  justify-content: center;
}

.cursor-arrow.visible {
  opacity: 1;
}

.cursor-arrow svg {
  width: 40px;
  height: 40px;
  fill: rgba(0, 0, 0, 0.8);
}

/* Zone styling - completely transparent */
.carousel-zone {
  background: transparent;
  border: none;
}

.carousel-zone-left {
  background: transparent;
  border: none;
}

.carousel-zone-right {
  background: transparent;
  border: none;
}

/* Zone hover effects - no visual changes */
.carousel-zone:hover {
  background: transparent;
  border: none;
}

